        while(scanf("%c",&c)&&c!='\n')
        {
            if(c=='A')p[i]|=1;
            else if(c=='B')p[i]|=2;
            else if(c=='C')p[i]|=4;
        }
    }
    for(int i=0;i<n;i++)
    {
        for(int j=7;j>=0;j--)
        {
            f[j|p[i]]=min(f[j|p[i]],f[j]+w[i]);
        }
    }
    cout<<(f[7]==0x3f3f3f3f?-1:f[7])<<endl;
    return 0;
}
```
## 暴力
昨天在场上直接暴力做了，就不具体解释暴力做法了，大家可以直接看源码：
```cpp
#include <iostream>
#include <algorithm>
#include <cstring>
using namespace std;
const int N = 1010;
typedef long long LL;
LL ans;
LL n;
LL min1[3];
LL min2[3]; // AB,AC,BC
LL min3 = 0x3f3f3f3f;
bool a = false, b = false, c = false;
bool find(string s, char x)
{
    bool res = false;
    for (int i = 0; i < s.size(); i++)
    {
        if (s[i] == x)
            return true;
    }
    return false;
}
int main()
{
    for (int i = 0; i < 3; i++)
    {
        min1[i] = min2[i] = 3e5;
    }
    cin >> n;

    for (int i = 0; i < n; i++)
    {
        LL p;
        string s;
        cin >> p >> s;
        sort(s.begin(), s.end());
        if (find(s, 'A'))
            a = true;
        if (find(s, 'B'))
            b = true;
        if (find(s, 'C'))
            c = true;
        int l = s.size();
        if (l == 1)
        {
            min1[s[0] - 'A'] = min(min1[s[0] - 'A'], p);
        }
        else if (l == 2)
        {
            if (s == "AB")
            {
                min2[0] = min(min2[0], p);
            }
            else if (s == "AC")
                min2[1] = min(min2[1], p);
            else
                min2[2] = min(min2[2], p);
        }
        else
            min3 = min(min3, p);
    }
    //	cout<<a<<b<<c<<endl;
    if (!a || !b || !c)
    {
        puts("-1");
        return 0;
    }

    LL ans1 = 0, ans2 = 0, ans3 = 0;
    LL ans4 = 0x3f3f3f3f;

    ans3 = min3;

    for (int i = 0; i < 3; i++)
    {
        ans1 += min1[i];
    }
    ans2 = min(min2[0] + min2[1], min2[1] + min2[2]);
    ans2 = min(ans2, min2[0] + min2[2]);

    for (int i = 0; i < 3; i++)
    {
        ans4 = min(min1[i] + min2[2 - i], ans4);
    }
    ans = min(ans1, ans2);
    ans = min(ans, ans3);
    ans = min(ans, ans4);
    cout << ans << endl;
    return 0;
}
```

